(()=>{document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll("[data-repo-type='github']").forEach(e=>{const n=e.dataset.repo,s=e.querySelector("[data-repo-stars]"),o=e.querySelector("[data-repo-forks]");if(!n||!s||!o)return;const i=`repoFacts:${n}`,t=JSON.parse(localStorage.getItem(i)||"null"),r=!t||Date.now()-t.fetched>10*60*60*1e3,a=e=>{e.stars&&(s.textContent=e.stars),e.forks&&(o.textContent=e.forks)};t&&!r?a(t):fetch(`https://api.github.com/repos/${n}`).then(e=>e.ok?e.json():Promise.reject(e.status)).then(e=>{const t={stars:e.stargazers_count,forks:e.forks_count};localStorage.setItem(i,JSON.stringify({...t,fetched:Date.now()})),a(t)}).catch(()=>{})})})})()